import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Use useNavigate for React Router v6
import "./index.css";

const SignUp = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const [message, setMessage] = useState("");
  const [isRegistered, setIsRegistered] = useState(false); // State to track registration success
  const navigate = useNavigate(); // Use navigate for routing

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password.length < 5) {
      setMessage("Password must be at least 5 characters long.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.text();
      if (response.ok) {
        setIsRegistered(true);  // Set state to true on successful signup
        setMessage("User created successfully! Please log in.");
        setTimeout(() => {
          navigate("/login");  // Use navigate to redirect to login page
        }, 2000);
      } else {
        setMessage(data);
      }
    } catch (error) {
      setMessage("Error signing up. Please try again.");
    }
  };

  return (
    <div className="signup-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />

        <label>Password:</label>
        <input type="password" name="password" value={formData.password} onChange={handleChange} required />

        <button type="submit">Register</button>
      </form>
      {message && <p className="message">{message}</p>}
      {isRegistered && (
        <p className="redirect-message">Redirecting to login...</p>  // Message before redirect
      )}
    </div>
  );
};

export default SignUp;
