import React, { useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import { FaEdit, FaTrash, FaCheck, FaTimes,FaPlus } from 'react-icons/fa';
import Cookies from 'js-cookie';
import './index.css';

const TaskDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate(); // Hook for redirection
  const [isCreating, setIsCreating] = useState(false);
  const [newTask, setNewTask] = useState({ title: '', description: '', due_date: '', status: 'pending' });
  const [selectedTask, setSelectedTask] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(null);

  useEffect(() => {
    const token = Cookies.get('jwt_token');
    if (!token) {
      navigate('/login');
    } else {
      fetchTasks();
    }
  }, []);

  const fetchTasks = async () => {
    try {
      const token = Cookies.get('jwt_token'); // Assuming you store token in localStorage
      const response = await fetch('http://localhost:3001/tasks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status !== 200) {
        Cookies.remove('jwt_token'); // Remove expired token
        navigate('/login'); // Redirect to login page
        return;
      }
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      console.log("data=",data)
      setTasks(data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleCreateTask = async () => {
    try {
      const token = Cookies.get('jwt_token');
      console.log("new task=",newTask)
      const response = await fetch('http://localhost:3001/tasks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(newTask),
      });
      console.log("response=",response)
      if (!response.ok) {
        throw new Error('Failed to create task');
      }
      const createdTask = await response.json();
      setTasks([...tasks, createdTask]);
      setIsCreating(false);
      setNewTask({ title: '', description: '', due_date: '' });
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setIsEditing(false);
  };

  const handleEditClick = (task) => {
    setEditedTask({ ...task });
    setIsEditing(true);
  };

  const handleEditSave = () => {
    setTasks(tasks.map(task => 
      task.id === editedTask.id ? editedTask : task
    ));
    setIsEditing(false);
    setSelectedTask(editedTask);
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
    setSelectedTask(null);
    setIsEditing(false);
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    ));
    if (selectedTask?.id === taskId) {
      setSelectedTask(prev => ({ ...prev, completed: !prev.completed }));
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-card">
        <div className="card-header">
          <h2 className="card-title">Task Dashboard</h2>
          <button className="create-task-button" onClick={() => setIsCreating(true)}>
            <FaPlus /> Create Task
          </button>
          {isCreating && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Create New Task</h3>
            <input
              type="text"
              placeholder="Title"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            />
            <input
              type="text"
              placeholder="Description"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
            />
            <input
              type="date"
              value={newTask.due_date}
              onChange={(e) => setNewTask({ ...newTask, due_date: e.target.value })}
            />
            <div className="modal-buttons">
              <button className="save-button" onClick={handleCreateTask}>Save</button>
              <button className="cancel-button" onClick={() => setIsCreating(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
        </div>
        <div className="card-content">
          <div className="dashboard-grid">
            {/* Task List */}
            <div className="task-list">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Due Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks.map(task => (
                    <tr 
                      key={task.id} 
                      className={`task-row ${task.completed ? 'completed' : ''}`}
                      onClick={() => handleTaskClick(task)}
                    >
                      <td>{task.title}</td>
                      <td>{task.description}</td>
                      <td>{task.due_date}</td>
                      <td className="status-cell">
                        <button
                          className={`status-button ${task.completed ? 'completed' : 'pending'}`}
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleTaskCompletion(task.id);
                          }}
                        >
                          {task.completed ? 
                            <FaCheck className="status-icon" /> : 
                            <FaTimes className="status-icon" />
                          }
                        </button>
                      </td>
                      <td className="actions-cell">
                        <button
                          className="action-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEditClick(task);
                          }}
                        >
                          <FaEdit />
                        </button>
                        <button
                          className="action-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteTask(task.id);
                          }}
                        >
                          <FaTrash />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Task Details */}
            <div className="task-details">
              {isEditing && editedTask ? (
                <div className="details-card">
                  <div className="card-header">
                    <h3 className="card-title">Edit Task</h3>
                  </div>
                  <div className="card-content">
                    <div className="form-container">
                      <div className="form-group">
                        <label htmlFor="title">Title</label>
                        <input
                          id="title"
                          type="text"
                          value={editedTask.title}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            title: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="description">Description</label>
                        <input
                          id="description"
                          type="text"
                          value={editedTask.description}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            description: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="dueDate">Due Date</label>
                        <input
                          id="dueDate"
                          type="date"
                          value={editedTask.dueDate}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            dueDate: e.target.value
                          })}
                        />
                      </div>
                      <div className="button-group">
                        <button
                          className="cancel-button"
                          onClick={() => setIsEditing(false)}
                        >
                          Cancel
                        </button>
                        <button
                          className="save-button"
                          onClick={handleEditSave}
                        >
                          Save Changes
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ) : selectedTask ? (
                <div className="details-card">
                  <div className="card-header">
                    <h3 className="card-title">Task Details</h3>
                  </div>
                  <div className="card-content">
                    <div className="details-container">
                      <div className="detail-group">
                        <label>Title</label>
                        <p className="detail-text">{selectedTask.title}</p>
                      </div>
                      <div className="detail-group">
                        <label>Description</label>
                        <p className="detail-text">{selectedTask.description}</p>
                      </div>
                      <div className="detail-group">
                        <label>Due Date</label>
                        <p className="detail-text">{selectedTask.dueDate}</p>
                      </div>
                      <div className="detail-group">
                        <label>Status</label>
                        <p className="detail-text">
                          {selectedTask.completed ? 'Completed' : 'Pending'}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="empty-state">
                  Select a task to view details
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashboard;
