import React, { useState } from 'react';
import { FaEdit, FaTrash, FaCheck, FaTimes } from 'react-icons/fa';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import './TaskDashboard.css';

const TaskDashboard = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: 'Complete Project Proposal',
      description: 'Write and submit the project proposal document',
      dueDate: '2025-03-01',
      completed: false,
    },
    {
      id: 2,
      title: 'Team Meeting',
      description: 'Weekly team sync meeting',
      dueDate: '2025-02-20',
      completed: true,
    },
  ]);

  const [selectedTask, setSelectedTask] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(null);

  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setIsEditing(false);
  };

  const handleEditClick = (task) => {
    setEditedTask({ ...task });
    setIsEditing(true);
  };

  const handleEditSave = () => {
    setTasks(tasks.map(task => 
      task.id === editedTask.id ? editedTask : task
    ));
    setIsEditing(false);
    setSelectedTask(editedTask);
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
    setSelectedTask(null);
    setIsEditing(false);
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    ));
    if (selectedTask?.id === taskId) {
      setSelectedTask(prev => ({ ...prev, completed: !prev.completed }));
    }
  };

  return (
    <div className="dashboard-container">
      <Card className="dashboard-card">
        <CardHeader>
          <CardTitle>Task Dashboard</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="dashboard-grid">
            {/* Task List */}
            <div className="task-list">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Due Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks.map(task => (
                    <tr 
                      key={task.id} 
                      className={`task-row ${task.completed ? 'completed' : ''}`}
                      onClick={() => handleTaskClick(task)}
                    >
                      <td>{task.title}</td>
                      <td>{task.dueDate}</td>
                      <td className="status-cell">
                        <Button
                          variant={task.completed ? "success" : "secondary"}
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleTaskCompletion(task.id);
                          }}
                        >
                          {task.completed ? 
                            <FaCheck className="status-icon completed" /> : 
                            <FaTimes className="status-icon pending" />
                          }
                        </Button>
                      </td>
                      <td className="actions-cell">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEditClick(task);
                          }}
                          className="action-button"
                        >
                          <FaEdit />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteTask(task.id);
                          }}
                        >
                          <FaTrash />
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Task Details */}
            <div className="task-details">
              {isEditing && editedTask ? (
                <Card>
                  <CardHeader>
                    <CardTitle>Edit Task</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="form-container">
                      <div className="form-group">
                        <Label htmlFor="title">Title</Label>
                        <Input
                          id="title"
                          value={editedTask.title}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            title: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <Label htmlFor="description">Description</Label>
                        <Input
                          id="description"
                          value={editedTask.description}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            description: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <Label htmlFor="dueDate">Due Date</Label>
                        <Input
                          id="dueDate"
                          type="date"
                          value={editedTask.dueDate}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            dueDate: e.target.value
                          })}
                        />
                      </div>
                      <div className="button-group">
                        <Button
                          variant="outline"
                          onClick={() => setIsEditing(false)}
                        >
                          Cancel
                        </Button>
                        <Button onClick={handleEditSave}>
                          Save Changes
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ) : selectedTask ? (
                <Card>
                  <CardHeader>
                    <CardTitle>Task Details</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="details-container">
                      <div className="detail-group">
                        <Label>Title</Label>
                        <p className="detail-text">{selectedTask.title}</p>
                      </div>
                      <div className="detail-group">
                        <Label>Description</Label>
                        <p className="detail-text">{selectedTask.description}</p>
                      </div>
                      <div className="detail-group">
                        <Label>Due Date</Label>
                        <p className="detail-text">{selectedTask.dueDate}</p>
                      </div>
                      <div className="detail-group">
                        <Label>Status</Label>
                        <p className="detail-text">
                          {selectedTask.completed ? 'Completed' : 'Pending'}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ) : (
                <div className="empty-state">
                  Select a task to view details
                </div>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default TaskDashboard;
