import React, { useState, useEffect } from 'react';
import { FaEdit, FaTrash, FaCheck, FaTimes, FaPlus } from 'react-icons/fa';
import './index.css';

const API_BASE_URL = 'http://localhost:3000/tasks'; // Replace with your actual API URL

const TaskDashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(null);
  const [isAddingTask, setIsAddingTask] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch all tasks
  const fetchTasks = async () => {
    try {
      setLoading(true);
      const token = Cookies.get('jwt_token'); // Get token from cookies

      const response = await fetch(API_BASE_URL, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`, // Attach the token in the request header
        },
      });
      if (response.status === 401) {
        // Token might be expired or invalid, redirect to login
        localStorage.removeItem('authToken');
        window.location.href = '/login'; // Redirect to login page
      }
      if (!response.ok) throw new Error('Failed to fetch tasks');

      const data = await response.json();
      setTasks(data);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };


  const createTask = async (taskData) => {
    try {
      setLoading(true);
      const token = Cookies.get('jwt_token'); // Get token from cookies

      const response = await fetch(API_BASE_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Attach the token in the request header
        },
        body: JSON.stringify(taskData),
      });
      if (response.status === 401) {
        // Token might be expired or invalid, redirect to login
        localStorage.removeItem('authToken');
        window.location.href = '/login'; // Redirect to login page
      }
      if (!response.ok) throw new Error('Failed to create task');
      await fetchTasks();
      setIsAddingTask(false);
      setNewTask({ title: '', description: '', dueDate: '' });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };


  // Update task
  const updateTask = async (id, taskData) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Attach the token in the request header
        },
        body: JSON.stringify(taskData),
      });
      if (response.status === 401) {
        // Token might be expired or invalid, redirect to login
        localStorage.removeItem('authToken');
        window.location.href = '/login'; // Redirect to login page
      }
      if (!response.ok) throw new Error('Failed to update task');
      await fetchTasks();
      setIsEditing(false);
      setSelectedTask(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/${id}`, {
        method: 'DELETE',
        headers: {

          'Authorization': `Bearer ${token}`, // Attach the token in the request header
        },
      });
      if (response.status === 401) {
        // Token might be expired or invalid, redirect to login
        localStorage.removeItem('authToken');
        window.location.href = '/login'; // Redirect to login page
      }
      if (!response.ok) throw new Error('Failed to delete task');
      await fetchTasks();
      setSelectedTask(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleAddTask = () => {
    setIsAddingTask(true);
    setIsEditing(false);
    setSelectedTask(null);
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    await createTask(newTask);
  };

  const handleUpdateTask = async (e) => {
    e.preventDefault();
    await updateTask(editedTask.id, editedTask);
  };

  const handleStatusToggle = async (task) => {
    const updatedTask = { ...task, completed: !task.completed };
    await updateTask(task.id, updatedTask);
  };

  if (loading) return <div className="loading-state">Loading...</div>;
  if (error) return <div className="error-state">Error: {error}</div>;

  return (
    <div className="dashboard-container">
      <div className="dashboard-card">
        <div className="card-header">
          <h2 className="card-title">Task Dashboard</h2>
          <button className="add-button" onClick={handleAddTask}>
            <FaPlus /> Add Task
          </button>
        </div>
        <div className="card-content">
          <div className="dashboard-grid">
            {/* Task List */}
            <div className="task-list">
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Due Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks.map(task => (
                    <tr 
                      key={task.id} 
                      className={`task-row ${task.completed ? 'completed' : ''}`}
                      onClick={() => {
                        setSelectedTask(task);
                        setIsEditing(false);
                        setIsAddingTask(false);
                      }}
                    >
                      <td>{task.title}</td>
                      <td>{task.dueDate}</td>
                      <td className="status-cell">
                        <button
                          className={`status-button ${task.completed ? 'completed' : 'pending'}`}
                          onClick={(e) => {
                            e.stopPropagation();
                            handleStatusToggle(task);
                          }}
                        >
                          {task.completed ? 
                            <FaCheck className="status-icon" /> : 
                            <FaTimes className="status-icon" />
                          }
                        </button>
                      </td>
                      <td className="actions-cell">
                        <button
                          className="action-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            setEditedTask({ ...task });
                            setIsEditing(true);
                            setIsAddingTask(false);
                          }}
                        >
                          <FaEdit />
                        </button>
                        <button
                          className="action-button"
                          onClick={(e) => {
                            e.stopPropagation();
                            deleteTask(task.id);
                          }}
                        >
                          <FaTrash />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Task Details/Forms */}
            <div className="task-details">
              {isAddingTask ? (
                <div className="details-card">
                  <div className="card-header">
                    <h3 className="card-title">Add New Task</h3>
                  </div>
                  <div className="card-content">
                    <form onSubmit={handleCreateTask} className="form-container">
                      <div className="form-group">
                        <label htmlFor="new-title">Title</label>
                        <input
                          id="new-title"
                          type="text"
                          value={newTask.title}
                          onChange={(e) => setNewTask({
                            ...newTask,
                            title: e.target.value
                          })}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="new-description">Description</label>
                        <input
                          id="new-description"
                          type="text"
                          value={newTask.description}
                          onChange={(e) => setNewTask({
                            ...newTask,
                            description: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="new-dueDate">Due Date</label>
                        <input
                          id="new-dueDate"
                          type="date"
                          value={newTask.dueDate}
                          onChange={(e) => setNewTask({
                            ...newTask,
                            dueDate: e.target.value
                          })}
                          required
                        />
                      </div>
                      <div className="button-group">
                        <button
                          type="button"
                          className="cancel-button"
                          onClick={() => setIsAddingTask(false)}
                        >
                          Cancel
                        </button>
                        <button
                          type="submit"
                          className="save-button"
                        >
                          Create Task
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              ) : isEditing && editedTask ? (
                <div className="details-card">
                  <div className="card-header">
                    <h3 className="card-title">Edit Task</h3>
                  </div>
                  <div className="card-content">
                    <form onSubmit={handleUpdateTask} className="form-container">
                      <div className="form-group">
                        <label htmlFor="edit-title">Title</label>
                        <input
                          id="edit-title"
                          type="text"
                          value={editedTask.title}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            title: e.target.value
                          })}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="edit-description">Description</label>
                        <input
                          id="edit-description"
                          type="text"
                          value={editedTask.description}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            description: e.target.value
                          })}
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="edit-dueDate">Due Date</label>
                        <input
                          id="edit-dueDate"
                          type="date"
                          value={editedTask.dueDate}
                          onChange={(e) => setEditedTask({
                            ...editedTask,
                            dueDate: e.target.value
                          })}
                          required
                        />
                      </div>
                      <div className="button-group">
                        <button
                          type="button"
                          className="cancel-button"
                          onClick={() => setIsEditing(false)}
                        >
                          Cancel
                        </button>
                        <button
                          type="submit"
                          className="save-button"
                        >
                          Save Changes
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              ) : selectedTask ? (
                <div className="details-card">
                  <div className="card-header">
                    <h3 className="card-title">Task Details</h3>
                  </div>
                  <div className="card-content">
                    <div className="details-container">
                      <div className="detail-group">
                        <label>Title</label>
                        <p className="detail-text">{selectedTask.title}</p>
                      </div>
                      <div className="detail-group">
                        <label>Description</label>
                        <p className="detail-text">{selectedTask.description}</p>
                      </div>
                      <div className="detail-group">
                        <label>Due Date</label>
                        <p className="detail-text">{selectedTask.dueDate}</p>
                      </div>
                      <div className="detail-group">
                        <label>Status</label>
                        <p className="detail-text">
                          {selectedTask.completed ? 'Completed' : 'Pending'}
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="empty-state">
                  Select a task to view details
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashboard;
